const LoginForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");





  const submit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log(username, password)
    let patternPassword = new RegExp("^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$");
    let patternUsername = new RegExp("^[a-zA-Z0-9](_(?!(\.|_))|\.(?!(_|\.))|[a-zA-Z0-9]){6,18}[a-zA-Z0-9]$");
    if(patternPassword.test(username) && patternUsername.test(username)){console.log('haj')}
    else{
      console.log('password or username is incorect')
    }
    const params = JSON.stringify({

      "username": username,

      "password": password,

      });
      const url = 'http://localhost:3001/login'
      const url1 = 'https://jsonplaceholder.typicode.com/todos'



    Axios.post('',params).then((response) => {
      console.log(response)
        if (response) {
            alert("Success!");
        } else {
            alert("Failed!");
        }
    }).catch(err=>{
      console.log(err)
    })



    // fetch("http://localhost:3001/login", {
